mapper.select:

select
bno, title, content, writer, regdate, views, recommendations, not_recommendations
from
	(
	select /*+INDEX_DESC(tbl_board pk_board) */
	rownum as rn, bno, title, content, writer, regdate, views, recommendations, not_recommendations
	from tbl_board 
	where rownum <= #{pageNum} * #{amount}
	)
where rn > (#{pageNum} - 1) * #{amount}

mapper.update:
update tbl_board
set title=#{title},
content=#{content},
writer=#{writer}
where bno=#{bno}
